const mysql=require("mysql");const MysqlData=require("../models/MysqlData");const connection=mysql.createConnection((new MysqlData).getConnection());module.exports=class PostList{constructor(){}read(offset,count){return new Promise((resolve,reject)=>{connection.query("SELECT p.`index`, p.`title`, p.`password`\n"+"FROM `posts` p\n"+"LEFT JOIN `accounts` a\n"+"ON p.`author`=a.`index`\n"+"ORDER BY p.`index` DESC\n"+"LIMIT ?, ?",[offset*count,count],(error,result,fields)=>{if(error){throw error}else{if(result.length>0){resolve(result)}else{reject("no-row")}}})})}getCount(){return new Promise(resolve=>{connection.query("SELECT COUNT(1) count\n"+"FROM `posts`;",(error,result,fields)=>{if(error){throw error}else{resolve(result[0].count)}})})}};