const mysql=require("mysql");const getMysqlConnectionData=require("../models/getMysqlConnectionData").getMysqlConnectionData;const connection=mysql.createConnection(getMysqlConnectionData());const jsStringEscape=require("js-string-escape");const sha512=require("js-sha512").sha512;const getSha512Salt=require("../models/getSha512Salt").getSha512Salt;function doAccountExist(id,password,callback){connection.connect();connection.query("SELECT * FROM accounts WHERE `id`=? AND `password`=?",[jsStringEscape(id),sha512(getSha512Salt()+jsStringEscape(password))],(error,results,fields)=>{if(error){throw error}connection.end();callback(results[0])})}module.exports={doAccountExist:doAccountExist};