const mysql=require("mysql");const getMysqlConnectionData=require("../models/getMysqlConnectionData").getMysqlConnectionData;const connection=mysql.createConnection(getMysqlConnectionData());const escapeHtml=require("escape-html");const Sha512=require("../lib/Sha512");const Aes256=require("../lib/Aes256");function doAccountExist(id,password){return new Promise((resolve,reject)=>{connection.query("SELECT * FROM accounts WHERE `id`=? AND `password`=?",[new Aes256(escapeHtml(id),"plain").getEncrypted(),new Sha512(password).getEncrypted()],(error,results,fields)=>{if(error){throw error}if(results.length>0){resolve(results[0])}else{reject("account-not-exist")}})})}module.exports={doAccountExist:doAccountExist};