const mysql=require("mysql");const MysqlData=require("../models/MysqlData");const connection=mysql.createConnection((new MysqlData).getConnection());function getPostPassword(postId){return new Promise((resolve,reject)=>{connection.query("SELECT `password`\n"+"FROM `posts`\n"+"WHERE `index`=?;",[postId],(error,result,fields)=>{if(error){throw error}if(result.length>0){resolve(result[0].password)}else{reject("no-row")}})})}module.exports={getPostPassword:getPostPassword};