const mysql=require("mysql");const MysqlData=require("../models/MysqlData");const connection=mysql.createConnection((new MysqlData).getConnection());function getPostTitle(index){return new Promise((resolve,reject)=>{connection.query("SELECT `title`\n"+"FROM `posts`\n"+"WHERE `index`=?;",[index],(error,result,fields)=>{if(error){throw error}if(result.length>0){resolve(result[0].title)}else{reject("no-row")}})})}module.exports={getPostTitle:getPostTitle};