const mysql=require("mysql");const getMysqlConnectionData=require("../models/getMysqlConnectionData").getMysqlConnectionData;const connection=mysql.createConnection(getMysqlConnectionData());function getPostTitle(index){return new Promise((resolve,reject)=>{connection.query("SELECT `title`\n"+"FROM `posts`\n"+"WHERE `index`=?;",[index],(error,result,fields)=>{if(error){throw error}if(result.length>0){resolve(result[0].title)}else{reject("no-row")}})})}module.exports={getPostTitle:getPostTitle};