const fetchPost=(postId,password)=>{fetch("https://community.bhsjp.kro.kr/get-post",{method:"post",headers:{"Content-Type":"application/json"},body:JSON.stringify({postId:postId,password:password,needComments:true})}).then(res=>{return res.json()}).then(obj=>{switch(obj.result){case"right":postNickname.innerText=obj.data.nickname;postDate.innerText=isoDateToKoreanDate(obj.data.date);postContent.innerHTML=obj.data.content;if(obj.data.isModified){title.innerHTML+=`<span class="post-edited">(수정됨)</span>`}for(i of obj.data.comments){commentsContainer.innerHTML+=`\n                        <div class="comments${i.isPrivate?" private-comment":""}">\n                            <div>\n                                ${i.nickname}&#58;&nbsp;${isoDateToKoreanDate(i.date)}\n                            </div>\n                            \n                            <div>\n                                ${i.content}\n                            </div>\n                            \n                            \n                        </div>\n                    `}break;case"wrong":vex.dialog.open({message:"비밀번호를 입력해주세요",input:`\n                        <input name="password" type="password">\n                    `,callback:data=>{if(data){fetchPost(postId,data.password)}else{history.back()}}});break;case"no-post":vex.dialog.alert({unsafeMessage:"존재하지 않는 글입니다\n전 페이지로 이동합니다",callback:()=>{history.back()}});break;case"error":vex.dialog.alert({unsafeMessage:"서버 에러가 발생했습니다",callback:()=>{vex.dialog.open({message:"비밀번호를 입력해주세요",input:`\n                                <input name="password" type="password">\n                            `,callback:data=>{if(data){fetchPost(postId,data.password)}else{history.back()}}})}});break}})};