const passwordToggle=document.getElementById("password-toggle");const passwordToggleIcon=passwordToggle.getElementsByTagName("i")[0];const password=document.getElementById("password");passwordToggle.addEventListener("click",()=>{if(passwordToggle.dataset.isShowing==="true"){passwordToggleIcon.innerHTML="radio_button_unchecked";password.type="password";passwordToggle.dataset.isShowing="false"}else{passwordToggleIcon.innerHTML="radio_button_checked";password.type="text";passwordToggle.dataset.isShowing="true"}});